import { CurrentUser } from '../../App';
import { UserActionTypes } from './user-types';

interface setCurrentUserAction {
  type: UserActionTypes.SET_CURRENT_USER;
  payload: CurrentUser;
}

export const setCurrentUser = (user: CurrentUser | null) => ({
  type: UserActionTypes.SET_CURRENT_USER,
  payload: user,
});
