import type { CurrentUser } from '../../interfaces';
import { UserActionTypes } from './user-types';

export interface SetCurrentUserAction {
  type: UserActionTypes.SET_CURRENT_USER;
  payload: CurrentUser;
}

export const setCurrentUser = (user: CurrentUser): SetCurrentUserAction => ({
  type: UserActionTypes.SET_CURRENT_USER,
  payload: user,
});

// Google SignIn
export const googleSignInStart = () => ({
  type: UserActionTypes.GOOGLE_SIGN_IN_START,
});

export const googleSignInSuccess = (user: CurrentUser) => ({
  type: UserActionTypes.GOOGLE_SIGN_IN_SUCCESS,
  payload: user,
});

export const googleSignInFailure = (error: any) => ({
  type: UserActionTypes.GOOGLE_SIGN_IN_FAILURE,
  payload: error,
});

// Email SignIn
export const emailSignInStart = (emailandpassword: any) => ({
  type: UserActionTypes.EMAIL_SIGN_IN_START,
  payload: emailandpassword,
});

export const emailSignInSuccess = (user: CurrentUser) => ({
  type: UserActionTypes.EMAIL_SIGN_IN_SUCCESS,
  payload: user,
});

export const emailSignInFailure = (error: any) => ({
  type: UserActionTypes.EMAIL_SIGN_IN_FAILURE,
  payload: error,
});
