import { ChangeEvent } from 'react';

interface FIProps {
  handleChange: (e: ChangeEvent<HTMLInputElement>) => void;
  name: string;
  label?: string;
  type: string;
  value: string;
  required: boolean;
}

const FormInput = ({ handleChange, label, ...otherProps }: FIProps) => {
  return (
    <div className='mx-0 my-[45px] relative'>
      <input
        className={`bg-blue-200 w-full block py-1 pr-1 pl-0.5 border-b-2 border-solid border-black outline-none ${
          otherProps.value.length ? '-top-14 text-xs text-black' : ''
        }`}
        onChange={handleChange}
        {...otherProps}
      />
      {label ? <label>{label}</label> : null}
    </div>
  );
};

export default FormInput;
