import { connect, ConnectedProps } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { selectShopCollections } from '../../redux/shop/shop-selector';

import CollectionPreview from '../../components/collection-preview/CollectionPreview';

export interface Collections {
  id: number;
  title: string;
  routeName: string;
  items: Item[];
}

export interface Item {
  id: number;
  name: string;
  imageUrl: string;
  price: number;
  quantity?: number;
}

interface ShopPageProps extends PropsFromRedux {}

const ShopPage = ({ collectons }: ShopPageProps) => {
  return (
    <div className='h-screen'>
      {collections.map(({ id, ...otherCollectionProps }) => (
        <CollectionPreview key={id} id={id} {...otherCollectionProps} />
      ))}
    </div>
  );
};

const mapStateToProps = createStructuredSelector({
  collectons: selectShopCollections,
});

const connector = connect(mapStateToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

export default connector(ShopPage);
