import { Reducer } from 'redux';

const INITAL_STATE = {
  currentUser: null,
};

const userReducer: Reducer = (state = INITAL_STATE, action) => {
  switch (action.type) {
    case 'SET_CURRENT_USER':
      return {
        ...state,
        currentUser: action.payload,
      };

    default:
      return state;
  }
};

export default userReducer;
