import * as React from 'react';

interface MIProps {
  title: string;
  imageUrl: string;
}

const MenuItem = ({ title, imageUrl }: MIProps) => (
  <div className='flex flex-auto items-center justify-center min-w-[30%] h-240px overflow-hidden border-1 border-solid border-black mt-1 mx-7 mb-5 border-2 border-black border-solid'>
    <div
      className='w-full h-full bg-cover bg-center'
      style={{ backgroundImage: `url(${imageUrl})` }}
    ></div>
    <div className='py-1 px-3 flex flex-col items-center justify-center h-90px border-2 border-black border-solid bg-white opacity-70 absolute'>
      <h1 className='text-semibold my-0 '>{title.toLocaleUpperCase()}</h1>
      <span className='text-base'>SHOP NOW</span>
    </div>
  </div>
);

export default MenuItem;
