import * as React from 'react';

interface MIProps {
  title: string;
  imageUrl: string;
}

const MenuItem = ({ title, imageUrl }: MIProps) => (
  <div className='flex flex-auto items-center justify-center min-w-[30%] h-240px overflow-hidden border-1 border-solid border-black mt-0 mx-7 mb-15'>
    <div
      className='w-full h-full'
      style={{ backgroundImage: `url(${imageUrl})` }}
    ></div>
    <div className='py-0 px-25 flex flex-column items-center justify-center h-90px '>
      <h1>{title.toLocaleUpperCase()}</h1>
      <span>SHOP NOW</span>
    </div>
  </div>
);

export default MenuItem;
